import React, { useEffect, useState } from 'react';

const Dashboard = () => {
  const [appointments, setAppointments] = useState([]);
  const [bmiHistory, setBmiHistory] = useState([]);
  const [dietPlans, setDietPlans] = useState([]);
  const [messages, setMessages] = useState([]);

  // Fetch data from API (Assuming an API endpoint for each section)
  useEffect(() => {
    fetchAppointments();
    fetchBMIHistory();
    fetchDietPlans();
    fetchMessages();
  }, []);

  const fetchAppointments = async () => {
    // Fetch appointments data
    const response = await fetch('/api/appointments');
    const data = await response.json();
    setAppointments(data);
  };

  const fetchBMIHistory = async () => {
    // Fetch BMI history data
    const response = await fetch('/api/bmi-history');
    const data = await response.json();
    setBmiHistory(data);
  };

  const fetchDietPlans = async () => {
    // Fetch diet plans
    const response = await fetch('/api/diet-plans');
    const data = await response.json();
    setDietPlans(data);
  };

  const fetchMessages = async () => {
    // Fetch messages
    const response = await fetch('/api/messages');
    const data = await response.json();
    setMessages(data);
  };

  return (
    <div className="dashboard">
      <h2>Customer Dashboard</h2>
      
      <section className="dashboard-section">
        <h3>Upcoming Appointments</h3>
        <ul>
          {appointments.map((appointment) => (
            <li key={appointment.id}>
              {appointment.date} at {appointment.time} with {appointment.dietitianName}
            </li>
          ))}
        </ul>
      </section>

      <section className="dashboard-section">
        <h3>BMI History</h3>
        <ul>
          {bmiHistory.map((entry, index) => (
            <li key={index}>
              Date: {entry.date} - BMI: {entry.bmi}
            </li>
          ))}
        </ul>
      </section>

      <section className="dashboard-section">
        <h3>Diet Plans</h3>
        <ul>
          {dietPlans.map((plan) => (
            <li key={plan.id}>
              {plan.name}: {plan.description}
            </li>
          ))}
        </ul>
      </section>

      <section className="dashboard-section">
        <h3>Messages</h3>
        <ul>
          {messages.map((message) => (
            <li key={message.id}>
              From: {message.senderName} - {message.text}
            </li>
          ))}
        </ul>
      </section>
    </div>
  );
};

export default Dashboard;
